//노예이름
const LOG_IN = "LOG_IN";
const LOG_OUT = "LOG_OUT";

//노예 생성함수
export const loginAction = (data) => ({ type: LOG_IN, data });
export const logoutAction = () => ({ type: LOG_OUT });

//생각날때마다 추가하자 state
const initialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {},
    },
    post: {
        mainPosts: [],
    }
}

//불변객체 -> 객체를 참조해서 수정 하면안된다. -->교체 가능 read만 가능.
//reducer는 (이전상태,액션)=>return 다음상태로 교체
export default rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN:
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    user: action.data,
                }
            }
        case LOG_OUT:
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    user: null,
                }
            }
    }
};
