import React, { useCallback } from 'react';
import { List, Button, Card } from "antd";
import PropTypes from "prop-types";
import { StopOutlined } from '@ant-design/icons';
const FollowingList = ({ header, data }) => {
    //리액트에서 배열안에 jsx 사용시 key 값 필수
    //리액트에서 컴포넌트의 props 에 함수로 들어가는 것은 useCallback 으로 최적화 해주자.
    const renderItem = useCallback(item => (
        <List.Item style={{ marginTop: 20 }}>
            <Card actions={[<StopOutlined key="stop" />]}>
                <Card.Meta description={item.nickname} />
            </Card>
        </List.Item>
    ));
    return (
        <List
            style={{ marginBottom: "20px" }}
            grid={{ gutter: 4, xs: 1, md: 3 }}
            size="small"
            header={<div>{header}</div>}
            loadMore={<div style={{ textAlign: "center", margin: "10px 0" }}><Button>더보기</Button></div>}
            bordered
            dataSource={data}
            renderItem={renderItem}
        />
    )
}
FollowingList.propTypes = {
    header: PropTypes.string.isRequired,
    data: PropTypes.array.isRequired
}

export default FollowingList;
