export const initalState = {
    isLoggingIn: false,//로그인 시도중
    isLoggedIn: false,
    isLoggingOut: false,//로그아웃 시도중 
    //시도중이 ture 면 로딩창을 띄울꺼임
    me: null,
    signUpData: {},
    loginData: {},
}

const reducer = (state = initalState, action) => {
    switch (action.type) {
        case LOG_IN_REQUEST:
            return {
                ...state,
                isLoggingIn: true,
            }
        case LOG_IN_SUCCESS:
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: { ...action.data, nickname: 'zerocho' },
            }
        case LOG_IN_FAILURE:
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: false,
            }
        case LOG_OUT_REQUEST:
            return {
                ...state,
                isLoggingOut: true,
            }
        case LOG_OUT_SUCCESS:
            return {
                ...state,
                isLoggingOut: false,
                isLoggedIn: false,
                me: null,
            }
        case LOG_OUT_FAILURE:
            return {
                ...state,
                isLoggingOut: false,
            }
        default: return state;
    }
}
export default reducer;
