//초기 앱에대한 state를 미리 잡아두어야한다.
const initialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {},
    },
    post: {

    }
}
//노예 생성함수
const changeNickname = data => ({ type: "CHANGE_NICKNAME", data });

//reducer는 (이전상태,액션)=>return 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'CHANGE_NICKNAME':
            return {
                ...state,
                name: action.data
            }
    }
};
export default rootReducer;
