import { createWrapper } from "next-redux-wrapper";
import { createStore, applyMiddleware, compose } from "redux";
import rootReducer from "../reducers";
import { composeWithDevTools } from 'redux-devtools-extension';
//composeWithDevTools 개발자 도구에서 보이게

//react에 redux를 붙이는건 react app에서는 단 하나의 redux store만 존재하므로 어렵지 않다.
//Next.js는 유저가 요청할때마다 redux store를 새로 생성한다.

const configureStore = () => {
    const enhancer = process.env.NODE_ENV === 'production'
        ? compose(applyMiddleware([])) //배포용 
        : composeWithDevTools(applyMiddleware([])) //개발용
    const store = createStore(rootReducer, composeWithDevTools);
    return store;
}

//createWrapper => next의 라이프사이클에 redux를 결합 시키는 역할
const wrapper = createWrapper(configureStore, {
    debug: process.env.NODE_ENV === 'development',
});

export default wrapper;
